package main

import (
	"testing"
)

func Test_characterReplacement(t *testing.T) {
	type args struct {
		s string
		k int
	}
	tests := []struct {
		name string
		args args
		want int
	}{
		{name: "Example 1", args: args{s: "ABAB", k: 2}, want: 4},
		{name: "Example 2", args: args{s: "AABABBA", k: 1}, want: 4},
		{name: "Case 13", args: args{s: "AAAB", k: 0}, want: 3},
		{name: "Case 24", args: args{s: "IMNJJTRMJEGMSOLSCCQICIHLQIOGBJAEHQOCRAJQMBIBATGLJDTBNCPIFRDLRIJHRABBJGQAOLIKRLHDRIGERENNMJSDSSMESSTR", k: 2}, want: 6},
		{name: "Case 28", args: args{s: "", k: 4567}, want: 5526},
		{name: "Case 30", args: args{s: "ABBB", k: 2}, want: 4},
		{name: "Custom 1", args: args{s: "AADBACBBEAF", k: 2}, want: 5},
		{name: "Custom 2", args: args{s: "", k: 821}, want: 0},
		{name: "Custom 3", args: args{s: "A", k: 208}, want: 1},
		{name: "Custom 4", args: args{s: "CCCCCCCCCCB", k: 0}, want: 10},
		{name: "Custom 5", args: args{s: "CCCCCCCCCCBBBD", k: 1}, want: 11},
		{name: "Custom 6", args: args{s: "AAAAABBBBBB", k: 3}, want: 9},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if got := characterReplacement(tt.args.s, tt.args.k); got != tt.want {
				t.Errorf("characterReplacement() = %v, want %v", got, tt.want)
			}
		})
	}
}
